mapper.select:
select

from seller_goods_info where shop_id = #{shopId}
order by status

select

from seller_goods_info where goods_id =
#{goodsId}
and shop_id =
#{shopId}

mapper.update:
update seller_goods_info


		status = #{status},


where shop_id = #{shopId}
and id in

	#{item}

update seller_goods_info


		category_id = #{categoryId},


where shop_id = #{shopId}
and id in

	#{item}

mapper.delete:
delete
from seller_goods_info
where id =#{id} and shop_id = #{shopId}
